import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import './index.css';


class Banner extends Component {
  render(){
    return (
      <div className = "Banner">
        <Link to = {this.props.link} className = "tituloLink"> {this.props.filme.nome} </Link>
        <Link to = {this.props.link} className = "imagemLink"> 
           <img src = {this.props.filme.foto} alt = {this.props.filme.nome} />
        </Link>
      </div>
    );
  }
}

export default Banner;
