import React, { Component } from 'react';
import './index.css';
import Banner from "./Banner/index";

class ListaFilmes extends Component {
  constructor(props){
    super(props);

    this.state = {
      filmes: []
    }

    this.consomeAPI = this.consomeAPI.bind(this);
  }

  consomeAPI(){
    let url = "https://sujeitoprogramador.com/r-api/?api=filmes";

    fetch(url)
      .then((res) => res.json())
        .then( (json) => {
          this.setState({filmes:json});
        })  
  }                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         

  componentDidMount(){
    this.consomeAPI();
  }

  render(){
    return (
      <div className = "containerListaFilmes">
        <article>
          {
          this.state.filmes.map((filme) => {
              return(
                  <div key = {filme.id}>                    
                    <Banner filme = {filme} link = {`/listaFilmes/${filme.id}`}/>
                  </div>
              );
          })
          }
        </article>
      </div> 
    );
  }
}

export default ListaFilmes;
