import React, { Component } from 'react';
import Botao from '../Botao/index';
import './index.css';

class Home extends Component {
  render(){
    return (
      <article className = "containerHome">
      {/*================================================================*/}
      <h1 className = "opcao">Escolha o gênero de filme que deseja assistir:</h1>
      {/* Container das Categorias da home */}
        <div className = "containerCategorias">          
          {/*=============================================*/}
          <div className = "containerBotoesCategoria">
            <Botao nome = "Ação" className = "botaoCategoria" link = "/listaFilmes"/>
            <Botao nome = "Ficção" className = "botaoCategoria" link = "/listaFilmes"/>
          </div>
          {/*=============================================*/}
          <div className = "containerBotoesCategoria">
            <Botao nome = "Suspense" className = "botaoCategoria" link = "/listaFilmes"/>
            <Botao nome = "Aventura" className = "botaoCategoria" link = "/listaFilmes"/>
          </div>
          {/*=============================================*/}

        </div>

      {/*================================================================*/}
      </article>
    );
  }
}

export default Home;
