import React, { Component } from 'react';
import './index.css';

class Filme extends Component {
    constructor(props){
        super(props);

        this.state = {
          id: '',
          filme: {}
        }


        this.getFilme = this.getFilme.bind(this);
    }

    getFilme(){
      let url = `https://sujeitoprogramador.com/r-api/?api=filmes/${this.state.id}`;

      fetch(url)
        .then((res) => res.json())
          .then( (json) => {
            this.setState({filme:json});
          })        
    }

    componentDidMount(){
      let id = this.props.match.params;

      this.setState({id:id});
      this.getFilme();
      console.log(this.state.filme);
    }

    render(){
          return (
            <div>
              <article>
                {console.log(this.state.filme)}
                <h2>{this.state.filme.nome}</h2>
                <img src = {this.state.filme.foto} alt = {this.state.filme.nome}></img>
                <p> {this.state.filme.sinopse} </p>
                <h6> {this.state.filme.sinopse} </h6>
              </article>
            </div>
          );
        }
      }

export default Filme;
